import React, { useEffect, useState } from 'react';
import { Dialog, DialogBackdrop, DialogPanel, DialogTitle } from '@headlessui/react';
import useStore from "./useStore";
import { useNavigate } from 'react-router-dom';

const Section = () => {
  const { filteredData, fetchPost, fetchLike, fetchComment } = useStore((state) => ({
    filteredData: state.filteredData,
    fetchPost: state.fetchPost,
    fetchLike: state.fetchLike,
    fetchComment: state.fetchComment,
  }));

  const user = JSON.parse(localStorage.getItem("user"));
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ baslik: "" });
  const [isCommentVisible, setIsCommentVisible] = useState(null); // Track which post's comment form is open
  const [comment, setComment] = useState(""); // Track comment text

  const handleCommentClick = (postId) => {
    setIsCommentVisible(isCommentVisible === postId ? null : postId); // Toggle visibility for specific post
  };

  const handleLikeClick = (postId, currentLikeCount) => {
    if (user) {
      const incrementedLikeCount = currentLikeCount + 1;
      fetchLike(postId, incrementedLikeCount);
    } else {
      navigate("/giris-yap");
    }
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault(); // Prevent default form submit behavior
    if (user) {
      const newComment = { ...formData, comment }; // Include comment in form data
      fetchComment(newComment);
      setComment(""); // Clear the comment input after submission
    } else {
      navigate("/giris-yap");
    }
  };

  useEffect(() => {
    fetchPost();
  }, [fetchPost]);

  return (
    <div>
      {/* Modal for users */}
      <Dialog open={open} onClose={setOpen} className="relative z-10">
        <DialogBackdrop
          transition
          className="fixed inset-0 bg-gray-500/75 transition-opacity"
        />
        <div className="fixed inset-0 z-10 w-screen overflow-y-auto">
          <div className="flex min-h-full items-end justify-center p-4 text-center sm:items-center sm:p-0">
            <DialogPanel
              transition
              className="relative transform overflow-hidden rounded-lg bg-white text-left shadow-xl transition-all"
            >
              <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <div className="flex w-full">
                  <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left w-full">
                    <DialogTitle as="h3" className="text-base font-semibold text-gray-900">
                      Kullanıcılar
                    </DialogTitle>
                    <ul>
                      <li>sdfsd</li>
                      <li>sdfsd</li>
                      <li>sdfsd</li>
                    </ul>
                  </div>
                </div>
              </div>
              <div className="bg-gray-50 px-4 py-3 sm:flex sm:flex-row-reverse sm:px-6"></div>
            </DialogPanel>
          </div>
        </div>
      </Dialog>

      {/* Displaying Post Form */}
      <p className="flex justify-between">
        <h4 className="font-semibold text-slate-800">En iyi diyet listesi</h4>
      </p>
      <br />
      {user && (
        <form className="w-full flex space-x-4 h-10" onSubmit={handleSubmit}>
          <div className="relative w-full">
            <input
              type="text"
              name="baslik"
              value={formData.baslik}
              onChange={handleChange}
              className="h-10 block w-full p-4 pl-10 text-sm text-gray-900 border border-gray-300 rounded-lg bg-gray-50 focus:ring-blue-500 focus:border-blue-500"
              placeholder="Başlık yaz..."
              required
            />
          </div>
          <button
            type="submit"
            className=" w-auto px-4 bg-purple-700 text-white rounded-lg hover:bg-purple-100 focus:ring-2 focus:ring-purple-300"
          >
            Gönder
          </button>
        </form>
      )}

      <br />
      <div className="grid grid-cols-1 md:grid-cols-1 gap-4">
        <div className="w-full p-4 h-[50vh] overflow-y-auto">
          {Array.isArray(filteredData) && filteredData.map((item) => {
            return (
              <div key={item._id} className="mt-2 text-gray-900">
                <h4>
                  <span className="font-semibold text-lg">Kullanıcı Adı:</span> {item.acikla}
                </h4>
                <p className="mt-1 text-sm text-gray-700">{item.baslik}</p>

                {/* Like Section */}
                <div className="flex items-center mt-2 space-x-4">
                  <button
                    className="text-purple-600 hover:text-purple-800"
                    onClick={() => handleLikeClick(item._id, item.likeCount)}
                  >
                    <span className="text-sm">Beğen</span>
                  </button>
                  <span className="text-sm text-gray-500">{item.likeCount} Beğeni</span>
                  <button
                    onClick={() => handleCommentClick(item._id)}
                    className="text-gray-500 hover:text-gray-700 focus:outline-none"
                  >
                    Yorum Yap
                  </button>
                </div>

                {/* Comment Section */}
                {isCommentVisible === item._id && (
                  <div className="mt-4">
                    <textarea
                      className="w-full p-2 border border-gray-300 rounded-lg text-sm"
                      placeholder="Yorumunuzu yazın..."
                      value={comment}
                      onChange={(e) => setComment(e.target.value)}
                    />
                    <button
                      onClick={() => {
                        if (comment.trim()) {
                          handleSubmit(); // Submit the comment if it's not empty
                        }
                      }}
                      className="absolute top-2 right-2 bg-gray-800 text-white px-4 py-2 rounded-lg hover:bg-gray-800 focus:outline-none"
                    >
                      Gönder
                    </button>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default Section;
