import React, { useState } from 'react';
import {Link} from "react-router-dom"
const LoginForm = () => {
  const [formData,setFormData]=useState(email="",firstName="",lastName="",password="")

  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Here you can add your form validation or API call for login
    if (!formData.email || !formData.password || !formData.firstName || !formData.lastName) {
      setErrorMessage('Lütfen tüm alanları doldurun.');
      return;
    }

    // Proceed with login logic
    console.log('firstName:', firstName);
    console.log('lastName:', lastName);
    console.log('Email:', email);
    console.log('Password:', password);

    // Clear error message
    setErrorMessage('');
  };
const handleChange=(e)=>{
  setFormData([e.target.name=e.target.value])
}
  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-semibold text-center text-purple-800">Kayıt Ol</h2>

        {/* Error Message */}
        {errorMessage && (
          <div className="text-red-500 text-sm text-center mt-2">
            {errorMessage}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-6 mt-4">
          {/* Email Input */}
          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700">
              İsim
            </label>
            <input
              type="email"
              id="email"
              value={formData.firstName}
              onChange={handleChange}
              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="İsim girin"
              required
            />
                   </div> <div>
             <label htmlFor="email" className="block text-sm font-medium text-gray-700">
              Soyisim
            </label>
            <input
              type="email"
              id="lastName"
              value={formData.lastName}
              onChange={handleChange}

              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Soyisim girin"
              required
            />
          </div>
          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700">
              E-posta
            </label>
            <input
              type="email"
              id="email"
              value={formData.email}
              onChange={handleChange}
              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="E-posta adresinizi girin"
              required
            />
          </div>

          {/* Password Input */}
          <div>
            <label htmlFor="password" className="block text-sm font-medium text-gray-700">
              Parola
            </label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Parolanızı girin"
              required
            />
          </div>
          <div className="mt-4 text-right">

        </div>
          {/* Submit Button */}
          <div>
            <button
              type="submit"
              className="w-full py-2 px-4 bg-purple-600 text-white font-semibold rounded-lg hover:bg-purple-500 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
             Kayıt Ol
            </button>
          </div>
        </form>

        <div className="mt-4 text-center">
          <p className="text-sm text-gray-600">
            Hesabınız var mı?{' '}
               <Link to="/giris-yap" className="text-purple-600 hover:text-purple-500">
               Giriş Yap
                        </Link>

          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
