import { create } from 'zustand'
import axios from "axios"
const useStore = create((set) => ({
    data: [],
    fectPost: async () => {
        try {
            const response = await axios.get('http://localhost:6099/panel'); // Replace with your API
            const data = await response.json();

            console.log('Fetched Data:', response); // Log the fetched data to see the response
            set((state) => {
                // Only update state if data has changed
                if (state.data) {
                    return { data: data };
                }

            });
        } catch (error) {
            console.error('Error fetching data:', error); // Log the error
        }
    },
}))

export default useStore
