import React, { useEffect, useState } from 'react'
import { Dialog, DialogBackdrop, DialogPanel, DialogTitle } from '@headlessui/react'
import useStore from '../useStore';
import { Link } from "react-router-dom"
import axios from "axios"

const Index = ({ open, setOpen, data }) => {
  const [base64Image, setBase64Image] = useState("");
  const { usersData, fetchUsers } = useStore();
  const user = JSON.parse(localStorage.getItem("user"));

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      setBase64Image(reader.result);
    };

    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleSave = async () => {
    try {
      await axios.patch(`http://localhost:6078/duzenle/${user.result.email}`, {
        selectedFile: base64Image,
      });
      alert("Profil fotoğrafı güncellendi!");
      setOpen(false);
    } catch (error) {
      console.error("Güncelleme hatası:", error);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  return (
    <Dialog open={open} onClose={setOpen} className="relative z-10">
      <DialogBackdrop className="fixed inset-0 bg-gray-500/75 transition-opacity" />

      <div className="fixed inset-0 z-10 w-screen overflow-y-auto">
        <div className="flex min-h-full items-end justify-center p-4 text-center sm:items-center sm:p-0">
          <DialogPanel className="relative transform overflow-hidden rounded-lg bg-white text-left shadow-xl transition-all sm:my-8 sm:w-full sm:max-w-lg">
            <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
              <div className="flex w-full">
                <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left w-full">
                  {usersData && (
                    <>
                      <DialogTitle as="h3" className="text-base font-semibold text-gray-900">
                        {data ? "Profil Fotoğrafını Güncelle" : "Soru Soran Kullanıcılar"}
                        <hr />
                      </DialogTitle>

                      <div className="mt-2 h-60 overflow-y-auto">
                        <ul>
                          {data ? (
                            <div className="flex flex-col items-center ">

                              {base64Image && (
                                <img
                                  src={base64Image}
                                  alt="Base64 Preview"
                                  className="w-40 h-40 rounded-full border-2 border-purple-500 object-cover mx-auto"
                                />
                              )}
                                             <input type="file" onChange={handleFileChange} />
                              <button
                                onClick={handleSave}
                                className="mt-2 px-4 py-2 bg-purple-600 text-white rounded hover:bg-purple-700"
                              >
                                Kaydet
                              </button>
                            </div>
                          ) : (
                            <>
                              {usersData.map((user, index) => (
                                <li key={index} className="text-sm text-gray-800 cursor-pointer hover:text-purple-500">
                                  <Link to={`/profile/${user._id}`}>@{user.firstName} {user.lastName}</Link>
                                </li>
                              ))}
                            </>
                          )}
                        </ul>
                      </div>
                    </>
                  )}
                </div>
              </div>
            </div>
            <div className="bg-gray-50 px-4 py-3 sm:flex sm:flex-row-reverse sm:px-6"></div>
          </DialogPanel>
        </div>
      </div>
    </Dialog>
  );
};

export default Index;
