import { create } from 'zustand';
import axios from 'axios';

const useStore = create((set) => ({
    data: [],  // Store the fetched data
    filteredData: [], // Store the filtered data
    search: "", // Store the search term

    // Fetch data from API
    fetchPost: async () => {
        try {
            const response = await axios.get('http://localhost:6099/panel');
            const fetchedData = response.data;

            // Get the current search term from state and filter the fetched data
            set((state) => {
                const filteredData = fetchedData.filter((item) =>
                    item.baslik.toLowerCase().includes(state.search.toLowerCase()) // Use search from state
                );

                return {
                    data: fetchedData,
                    filteredData: filteredData, // Store the filtered data
                };
            });
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    },

    // Update search term and filter data accordingly
    setSearch: (newSearch) => {
        set((state) => {
            const filteredData = state.data.filter((item) =>
                item.title.toLowerCase().includes(newSearch.toLowerCase()) // Filter based on new search term
            );
            return {
                search: newSearch,
                filteredData: filteredData, // Update filtered data
            };
        });
    },

    // Update like count for a post
    fetchLike: async (postId, incrementValue) => {
        try {
            await axios.put(`http://localhost:6099/panel/like/${postId}`);
            set((state) => {
                // Update the like count for the post with the given postId
                const updatedPosts = state.data.map((post) =>
                    post._id === postId ? { ...post, likeCount: incrementValue } : post
                );
                return {
                    data: updatedPosts, // Update the data with the new like count
                };
            });
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    },
}));

export default useStore;
