import { create } from 'zustand'
import axios from "axios"

const useStore = create((set) => ({
    data: [],  
    fetchPost: async () => { 
        try {
            const response = await axios.get('http://localhost:6099/panel'); 
            set({
                data: response.data, // Storing the fetched data in 'data' field
            });
        } catch (error) {
            console.error('Error fetching data:', error); // Log the error
        }
    },
    fetchLike: async (id,post) => { 
        try {
            const response = await axios.put(`/panel/like/${id}`,post); 
            set((state) => {
                const updatedPosts = state.data.map((p) =>
                    p.id === id ? { ...p, likes: response.data.likes } : p
                );
                return {
                    data: updatedPosts, // Update the state with the updated list of posts
                };
            });
        } catch (error) {
            console.error('Error fetching data:', error); // Log the error
        }
    },
}))

export default useStore
