import React, { useState, useEffect } from 'react'
import Dialog from "../../components/Dialog"
import { useNavigate } from 'react-router-dom';
import useStore from '../../components/useStore';

const Header = () => {
  const [open, setOpen] = useState(false)
  const { users, fetchUsers } = useStore()
  const [usersData, setUsers] = useState([])
  const user = JSON.parse(localStorage.getItem("user"));

  const navigate = useNavigate()

  // Handle the "Kullanıcılar" click
  const handleClickUsers = async () => {
    if (user) {
      setOpen(true)
      await fetchUsers() // Make sure to fetch the users first
      setUsers(users) // Set the users data
    } else {
      navigate("/giris-yap") // Redirect to login page if no user
    }
  }

  // Handle the "Etiket ara" click
  const handleClick = () => {
    if (user) {
      setOpen(true)
    } else {
      navigate("/giris-yap")
    }
  }

  useEffect(() => {
    // This ensures users are fetched when the component is first mounted
    fetchUsers()
  }, [fetchUsers])

  return (
    <div className='border-r-2 border-gray-100 md:col-span-2'>
      <h4 className="font-semibold text-slate-800">Menü</h4>
      <div className='grid grid-cols-1'>
        {/* Dialog should use usersData */}
        <Dialog setOpen={setOpen} open={open} usersData={usersData} />
        <ul>
          <li onClick={handleClickUsers}>Kullanıcılar</li>
          <li onClick={handleClick}>Etiket ara</li>
        </ul>
      </div>
    </div>
  )
}

export default Header
