import { create } from 'zustand'

const useStore = create((set) => ({
    data: [],
    fectPost: async () => {
        try {
            const response = await fetch('http://localhost:6000/panel'); // Replace with your API
            const data = await response.json();

            console.log('Fetched Data:', data); // Log the fetched data to see the response
            set((state) => {
                // Only update state if data has changed
                if (JSON.stringify(state.data) !== JSON.stringify(data)) {
                    return { data: data };
                }
                return state; // Return current state if data is the same
            });
        } catch (error) {
            console.error('Error fetching data:', error); // Log the error
        }
    },
}))

export default useStore
