import { create } from 'zustand';
import axios from 'axios';

const useStore = create((set) => ({
    data: [],  // Store the fetched data
    veri: [],
    filteredData: [], // Store the filtered data
    search: "", // Store the search term

    // Fetch data from API
    fetchPost: async () => {
        try {
            const response = await axios.get('http://localhost:6099/panel');
            const fetchedData = response.data;

            // Get the current search term from state and filter the fetched data
            set((state) => {
                const filteredData = fetchedData.filter((item) =>
                    item.baslik.toLowerCase().includes(state.search.toLowerCase()) // Use search from state
                );

                return {
                    data: fetchedData,
                    filteredData: filteredData, // Store the filtered data
                };
            });
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    },

    // Update search term and filter data accordingly
    setSearch: (newSearch) => {
        set((state) => {
            const filteredData = state.data.filter((item) =>
                item.baslik.toLowerCase().includes(newSearch.toLowerCase()) // Filter based on new search term
            );
            return {
                search: newSearch,
                filteredData: filteredData, // Update filtered data
            };
        });
    },

    fetchLike: async (postId, incrementValue) => {
        try {
            await axios.put(`http://localhost:6099/panel/like/${postId}`);
            set((state) => {
                const updatedPosts = state.filteredData.map((post) =>
                    post._id === postId ? { ...post, likeCount: incrementValue } : post
                );
                return {
                    filteredData: updatedPosts,
                };
            });
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    },
    fetchComment: async (formData) => {
        try {
            const response = await axios.post("http://localhost:6099/panel", formData); // Use POST if required by the backend

            // Assuming the response contains the new post or comment
            const newPost = response.data; // Assuming response contains the new post data

            set((state) => {

                const updatedFilteredData = [...state.filteredData, newPost]; // Add to filteredData if necessary
                return {

                    filteredData: updatedFilteredData, // Optionally update filtered data if necessary
                };
            });
        } catch (error) {
            console.error('Error submitting comment:', error);
        }
    },
    fetchRegister: async (formData) => {
        console.log(formData)
        try {
            await axios.post("http://localhost:6099/uye-ol", formData); // Use POST if required by the backend


        } catch (error) {
            console.error('Error submitting comment:', error);
        }
    }
}));

export default useStore;
