import React, { useState } from 'react';
import useStore from "../components/useStore";
import { useNavigate } from 'react-router-dom';
const LoginForm = () => {
  const {fetchSifre}=useStore()
  const [formData,setFormData]=useState({newPassword:"",password:""})
  const navigate=useNavigate()
const handleChange=(e)=>{
  setFormDatasetFormData({
    ...formData,
    [e.target.name]: e.target.value,
  });
}
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Here you can add your form validation or API call for login
    if (!formData.password || !formData.newPassword) {
      setErrorMessage('Lütfen tüm alanları doldurun.');
      return;
    }

    if (formData.password !== formData.newPassword) {
      setErrorMessage('Şifreler uyusmuyor.');
      return;
    }
    console.log(formData)
    fetchSifre(formData)

    setErrorMessage("Başarılı yönlendiriliyorsunuz :)");
    setTimeout(() => {
      navigate("/giris-yap");
    }, 2000); 
    // Clear error message
    setErrorMessage('');
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
        <h2 className="text-2xl font-semibold text-center text-purple-800">Şifremi Unuttum</h2>

        {/* Error Message */}
        {errorMessage && (
          <div className="text-red-500 text-sm text-center mt-2">
            {errorMessage}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-6 mt-4">



          {/* Password Input */}
          <div>
            <label htmlFor="password" className="block text-sm font-medium text-gray-700">
              Parola
            </label>
            <input
                  name='password'
              value={formData.password}
              onChange={handleChange}
              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Parolanızı girin"
              required
            />
          </div>
          <div className="mt-4 text-right">

        </div>
        <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-700">
             Parola Tekrar
            </label>
            <input
              name='newPassword'
              value={formData.newPassword}
              onChange={handleChange}
              className="mt-1 block w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Parola Tekrar"
              required
            />
          </div>
          <div>
            <button
              type="submit"
              className="w-full py-2 px-4 bg-purple-600 text-white font-semibold rounded-lg hover:bg-purple-500 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
             Kaydet
            </button>
          </div>
        </form>


      </div>
    </div>
  );
};

export default LoginForm;
