import React, { useState, useEffect } from 'react'
import Dialog from "../../components/Dialog"
import { useNavigate } from 'react-router-dom';
import useStore from '../../components/useStore';

const Header = () => {
  const [open, setOpen] = useState(false)
  const { usersData, fetchUsers } = useStore()
  const [users, setUsers] = useState([])
  const user = JSON.parse(localStorage.getItem("user"));

  const navigate = useNavigate()
const data=usersData ? usersData :""
const fetchusers=fetchUsers() ? fetchUsers() : ""
  // Handle the "Kullanıcılar" click
  const handleClickUsers = async () => {
    if (user) {
      setOpen(true)
      await fetchusers() // Make sure to fetch the users first
      setUsers(data) // Set the users data

    } else {
      navigate("/giris-yap") // Redirect to login page if no user
    }
  }


  useEffect(() => {
    // This ensures users are fetched when the component is first mounted
    fetchusers()
  }, [fetchUsers])

  return (
    <div className='border-r-2 border-gray-100 md:col-span-2'>
      <h4 className="font-semibold text-slate-800">Menü</h4>
      <div className='grid grid-cols-1'>
        {/* Dialog should use usersData */}
        <Dialog setOpen={setOpen} open={open} users={users} />
        <ul>
          <li onClick={handleClickUsers}>Kullanıcılar</li>

        </ul>
      </div>
    </div>
  )
}

export default Header
